sourcesContent:
import redisClient from './redis'; // Assuming Redis is set up for sessions

// Function to extract userId from session (request.session)
export const getIdAndKey = async (request) => {
  const { userId } = request.session;

  if (!userId) {
    throw new Error('User not authenticated');
  }

  // Optionally, retrieve additional session data from Redis
  const key = await redisClient.get(`session:${userId}`);

  return { userId, key };
};

// Function to validate if userId is valid
export const isValidUser = async (userId) => {
  if (!userId) return false;

  // Optionally, check if the user exists in Redis or MongoDB
  const userExists = await redisClient.exists(`session:${userId}`);

  return userExists > 0;
};
